def start_of_packet(signal):
    for index in range(0, len(signal)-3):
        if len(set(list(signal[index: index+4]))) == 4:
            return index + 4


def start_of_message(signal):
    for index in range(0, len(signal)-13):
        if len(set(list(signal[index: index+14]))) == 14:
            return index + 14
    return -1


assert start_of_packet('bvwbjplbgvbhsrlpgdmjqwftvncz') == 5
assert start_of_packet('nppdvjthqldpwncqszvftbrmjlhg') == 6
assert start_of_packet('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 10
assert start_of_packet('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 11

signal_input = ''
print(f'We need to process {start_of_packet(signal_input)} characters before the first start-of-packet marker.')

assert start_of_message('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 19
assert start_of_message('bvwbjplbgvbhsrlpgdmjqwftvncz') == 23
assert start_of_message('nppdvjthqldpwncqszvftbrmjlhg') == 23
assert start_of_message('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg') == 29
assert start_of_message('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw') == 26

print(f'We need to process {start_of_message(signal_input)} characters before the first start-of-message marker.')
